INSERT [dbo].[ContentLocale] ([ContentID], [CultureID], [Title], [Body], [MetaTitle], [MetaKeyword], [MetaDescription], [UrlName]) VALUES (3, 1, N'Link To Us', N'<TABLE cellSpacing=0 cellPadding=10 width="100%" border=0><TBODY><TR><TD><DIV style="TEXT-ALIGN: left"><B>Webmasters:</B> If you have an <FONT class=NoteText>Your Information Here</FONT> that you would like to be featured, please email us your link information, and an editor will visit your site and consider it for addition.<BR>&nbsp;&nbsp;&nbsp;&nbsp; Once you have submitted your information, please use text link below for linking to us. After receiving your request, we will check your site within 48 hours, and then send a confirmation email if your site has been approved for inclusion.<BR><BR><BR><TABLE style="FONT-WEIGHT: bold"><TBODY><TR><TD vAlign=top>1.</TD><TD>FIRST ADD A LINK TO OUR SITE! Please use the text link below.</TD></TR><TR><TD vAlign=top>2.</TD><TD>Be sure to click on it at least once. That will make it show up in my logs.We add every site we see in our logs.</TD></TR><TR><TD vAlign=top>3.</TD><TD>Also be sure to <FONT class=NoteText>email us.</FONT> That way we ll will know what title, description, category(optional), you want for your site.</TD></TR></TBODY></TABLE><BR><BR><DIV class=NoteText style="TEXT-ALIGN: center">NOTE: we do NOT accept sites containing sexual contents!</DIV><BR><DIV style="TEXT-ALIGN: center"><?xml:namespace prefix = asp /><asp:textbox id=uxText runat="server" borderstyle="Ridge" height="97px" readonly="True" textmode="MultiLine" width="450px">&lt;a href="http://YOURURL.com"&gt;Your title here&lt;/a&gt; Your Description Here.</asp:textbox>&nbsp;</DIV><BR><HR><BR><DIV style="COLOR: blue">Or you can use the following data:</DIV><BR><B>TITLE:</B>Your Title Here<BR><B>URL:</B> Your URL Here<BR><B>DESCRIPTION:</B> Your Description Here.<BR><BR><DIV style="COLOR: blue">Here is HTML code for the above information:</DIV><BR><BR>&lt;a href="Your URL HERE"&gt;YOUR TITLE&lt;/a&gt;YOUR DESCRIPTION.<BR><BR><DIV style="TEXT-ALIGN: center">Thank you for exchanging link with us!!</DIV><BR><DIV style="COLOR: blue; TEXT-ALIGN: center">email@yourcompany.com</DIV></DIV></TD></TR></TBODY></TABLE>', NULL, NULL, NULL, N'LinkToUs')
INSERT [dbo].[ContentLocale] ([ContentID], [CultureID], [Title], [Body], [MetaTitle], [MetaKeyword], [MetaDescription], [UrlName]) VALUES (4, 1, N'Affiliate', N'<b>AFFILIATE AGREEMENT</b><br /> <br />
